import React,{ useState } from "react";
import { useHistory, useParams } from "react-router-dom";
import { useDispatch } from "react-redux";




const Form = () => {

  const [title, setTitle] = useState('')
  const [body, setBody] = useState('')

  //url route
  let history = useHistory()
  const dispatch = useDispatch()

  //url param id
  let {id} = useParams()





  const submit = (event)=>{

    console.log('submit event ---------------')
    event.preventDefault()

    const obj={
      title: title,
      body: body,
      id: id
    }

    if(id){
      dispatch({
        type:"Update_note",
        value: obj
      })
    }
    else{
      dispatch({
        type: "Add_note",
        value: obj
      })
    }
    history.push('')  
    event.preventDefault()
  }




  return(
    <div className="main-container">
      <form onSubmit={submit}>
        <label htmlFor="title">title</label>
        <div class="field">
          <div class="control">
            <input class="input is-small" 
            onChange={e=>setTitle(e.target.value)} name="title" value={title}
            type="text" placeholder="Small input" id="title"/>
          </div>
        </div>

        <label htmlFor="body">body</label>
        <div class="field">
          <div class="control">
            <textarea class="textarea is-small" 
            onChange={e=>setBody(e.target.value)} name="body" value={body}
            placeholder="Small textarea" id="body"></textarea>
          </div>
        </div>

        <div class="field is-grouped is-pulled-right">
          <div class="control">
            <button class="button is-small is-text">Cancel</button>
            <button class="button is-small is-primary">Submit</button>
          </div>
        </div>
      </form>
    </div>
  )
}


export default Form
