import React, { Component } from 'react';
import { connect } from 'react-redux';


class FormDetail extends Component {
  constructor(props) {
    super(props);
      this.state={
        obj:{
          title: '',
          body: ''
        }
      }
    }


 onChange = (event)=>{

   this.setState({
     [event.target.name]:event.target.value
   }) 

   console.log(this.state.obj)
 }


  render() {
    return (
     <div className="main-container">
       <form>
         <label htmlFor="title">title</label>
         <div class="field">
           <div class="control">
             <input class="input is-small" 
             onChange={this.onChange}
             type="text" placeholder="Small input" id="title"/>
           </div>
         </div>

         <label htmlFor="body">body</label>
         <div class="field">
           <div class="control">
             <textarea class="textarea is-small" 
             placeholder="Small textarea" id="body"></textarea>
           </div>
         </div>

         <div class="field is-grouped is-pulled-right">
           <div class="control">
             <button class="button is-small is-text">Cancel</button>
             <button class="button is-small is-primary">Submit</button>
           </div>
         </div>
       </form>
     </div>
    );
  }
}

const reciver = (state)=>{
  return{
    ary: state.ary
  }
}

export default connect(reciver)(FormDetail);
