import React, { Component } from 'react';
import { connect } from 'react-redux';

//style
import "../style/notelistStyle.scss";
import NoteCard from './notecard';


class NoteList extends Component {
  constructor(props) {
    super(props);
      this.state={
        ary:[]
      }
      // this.editCall_ = this.editCall_.bind(this)
    }

  componentWillMount(){
    this.setState({
      ary: this.props.ary
    })
  }

  getObj(event){
    const { param } = event.target.dataset;
    console.log(param)
  }


  editCall_ = (item) => {
        if (this.props.onItemClick) {
            this.props.onItemClick(item);
        }
    }

  render() {
    return (
      <div className="main-container">


        <div className="search-bar-container">
          <div className="control has-icons-left has-icons-right">
            <input className="input is-small" type="email" placeholder="Search.."/>
            <span className="icon is-small is-left">
              <i className="fas fa-envelope"></i>
            </span>
            <span className="icon is-small is-right">
              <i className="fas fa-check"></i>
            </span>
          </div>
        </div>

        <div className="notes-list">
          {
            this.props.ary.map((res,index)=>(
              <NoteCard title={res.title} body={res.body} value={res.title} key={res._id} editCall={this.editCall_}/> 
            ))
          }
        </div>

        <button className="button floatin-add-button" onClick={()=>this.props.history.push('/addlist')}>+ Add</button>
      </div>
    );
  }
}

const reciver = (state)=>{
  return{
    ary: state.ary,
  }
}

export default connect(reciver)(NoteList);
