import React, { Component } from 'react';
import { connect } from 'react-redux';


class FormDetail extends Component {
  constructor(props) {
    super(props);
      this.state={
          title: '',
          body: ''
      }
    }


 onChange = (event)=>{

    console.log(event.target.name)
    console.log(event.target.value)
   this.setState({
     [event.target.name]:event.target.value
   }) 
   console.log(this.state.obj)
 }



 submitCall = (e) => {
    console.log(this.state.obj)
    e.preventDefault()
 }

  render() {
    return (
     <div className="main-container">
       <form onSubmit={this.submitCall}>
         <label htmlFor="title">title</label>
         <div class="field">
           <div class="control">
             <input class="input is-small" 
             onChange={this.onChange}
             type="text" placeholder="Small input" name="title"/>
           </div>
         </div>

         <label htmlFor="body">body</label>
         <div class="field">
           <div class="control">
             <textarea class="textarea is-small" 
             onChange={this.onChange}
             placeholder="Small textarea" name="body"></textarea>
           </div>
         </div>

         <div class="field is-grouped is-pulled-right">
           <div class="control">
             <button class="button is-small is-text">Cancel</button>
             <button class="button is-small is-primary">Submit</button>
           </div>
         </div>
       </form>
     </div>
    );
  }
}

const reciver = (state)=>{
  return{
    ary: state.ary
  }
}

export default connect(reciver)(FormDetail);
