import React, { Component } from 'react';
import { connect } from 'react-redux';


class FormDetail extends Component {

  constructor(props) {
    super(props);
      this.state={
        obj:{
          title: '',
          body: ''
        }
      }
    }

    onChange = (e) => {
      let _ = this.state.obj;
      _[e.target.name] = e.target.value;
      this.setState({ obj: _ });
    }

    submitCall = (e) => {
      e.preventDefault()

      const { dispatch } = this.props;                
      dispatch({
        type: 'Add_note',
        value: this.state.obj
      });
      reciver()



      return (dispatch, reciver) => {

        dispatch({
          type: 'Add_note',
          value: this.state.obj
        });
        const {ary} = reciver();
        console.log(ary)
      }

    }

  render() {


    return (
     <div className="main-container">
       <form onSubmit={this.submitCall}>
         <label htmlFor="title">title</label>
         <div class="field">
           <div class="control">
             <input class="input is-small" 
             onChange={this.onChange}
             type="text" placeholder="Small input" name="title"/>
           </div>
         </div>


         <label htmlFor="body">body</label>
         <div class="field">
           <div class="control">
             <textarea class="textarea is-small" 
             onChange={this.onChange}
             placeholder="Small textarea" name="body"></textarea>
           </div>
         </div>

         <div class="field is-grouped is-pulled-right">
           <div class="control">
             <button class="button is-small is-text">Cancel</button>
             <button class="button is-small is-primary">Submit</button>
           </div>
         </div>
       </form>
       {JSON.stringify(this.props)}
     </div>
    );
  }
}

const reciver = (state)=>{
  return{
    ary: state.ary
  }
}

export default connect(reciver)(FormDetail);
