const initialState = {
  ary: [
       { _id: 1,
        title: 'Code Nicely',
        body: 'hi i m prince working as front end devloper in code nicely'
       },
       {
        _id: 2,
        title: 'Smileboat',
        body: 'hi i m prince working as front end devloper in code nicely'
       },
  ]
}

const reducer = (state=initialState,action)=>{

  switch(action.type){

    case "add_note" : 
      Object.assign(action.value,{_id: state.ary.length+1})

      return{
        ary: state.ary.concat(action.value)
      }
    case "update_note" :
      console.log('update method call now.......')

      console.log(state.ary.filter(res=>res._id === action.value))
      return{
         ary: state.ary.filter(res=>res._id === action.value)
      }

    default:  
       return state  
  }
}
export default reducer
