import React from "react";

//routing
import { BrowserRouter, Route } from "react-router-dom";
import NoteList from "./notelist";
import FormDetail from "./formdetail";


import "../index.scss";

//component


const Layout = ({children})=>{
  return(
    <div> 
     <div>


     <div class="field">
     <div class="control is-small is-loading">
       <textarea class="textarea is-small" placeholder="Small loading textarea"></textarea>
     </div>
   </div>
   <div class="field">
     <div class="control is-loading">
       <textarea class="textarea" placeholder="Normal loading textarea"></textarea>
     </div>
   </div>
   <div class="field">
     <div class="control is-medium is-loading">
       <textarea class="textarea is-medium" placeholder="Medium loading textarea"></textarea>
     </div>
   </div>
   <div class="field">
     <div class="control is-large is-loading">
       <textarea class="textarea is-large" placeholder="Large loading textarea"></textarea>
     </div>
   </div>


       {children}
     </div>

     <BrowserRouter>
       <Route path="/" exact component={NoteList}></Route>
       <Route path="/addlist" exact component={FormDetail}></Route>
       <Route path="/addlist/:id" exact component={FormDetail}></Route>
     </BrowserRouter>

    </div>
  )
}
export default Layout;
