const initialState = {
  ary: [
    {   _id: 1,
        title: 'Code Nicely',
        body: 'hi i m prince working as front end devloper in code nicely'
       },
       {
        _id: 1,
        title: 'Smileboat',
        body: 'hi i m prince working as front end devloper in code nicely'
       }
  ]
}

const reducer = (state=initialState,action)=>{

  switch(action.type){
    case "Add_note" : 
       Object.assign(action.value,{_id: state.ary.length+1})
       state.ary.push(action.value)
       return state
       case "Update_note" : 

        let obj = state.ary[action.value-1]
       return obj
    default:  
       return state  
  }
}
export default reducer
